import React, {useState} from "react";

const AuthContext = React.createContext({
    isLoggedIn:false,
    onLogout: ()=>{},
});

export const AuthContextProvider = (props) => {
    return <AuthContext.Provider>
        {props.children}
    </AuthContext.Provider>
}

export default AuthContext;
