import React, {useState} from "react";

const AuthContext = React.createContext({
    isLoggedIn:false,
    onLogout: ()=>{},
});

export const AuthContextProvider = (props) => {
    const [isLoggedIn,setIsLoggedIn] = useState(false);

    const logoutHandler = () => {
        setIsLoggedIn(false);
    }
    const loginHandler = () => {
        setIsLoggedIn(true);
    }
    return <AuthContext.Provider value={{isLoggedIn: isLoggedIn, onLogout: logoutHandler, onLogin: logoutHandler}}>
        {props.children}
    </AuthContext.Provider>
}

export default AuthContext;
